mapper.update:
UPDATE BID
BID_PRICE = #{bidPrice}, BID_CONTACT_NAME = #{bidContactName}, BID_CONTACT_PHONE = #{bidContactPhone}, BID_STATUS = #{bidStatus}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE REQ_CD = #{reqCd} AND BID_UID = #{bidUid} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  REQ_CD AS "reqCd",
  BID_UID AS "bidUid",
  BID_PRICE AS "bidPrice",
  BID_CONTACT_NAME AS "bidContactName",
  BID_CONTACT_PHONE AS "bidContactPhone",
  BID_STATUS AS "bidStatus",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM BID
WHERE 1 = 1 
  AND REQ_CD = #{reqCd}
  AND BID_UID = #{bidUid}
  AND BID_PRICE = #{bidPrice}
  AND BID_CONTACT_NAME = #{bidContactName}
  AND BID_CONTACT_PHONE = #{bidContactPhone}
  AND BID_STATUS = #{bidStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM BID
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND REQ_CD = #{reqCd}
  AND BID_UID = #{bidUid}
  AND BID_PRICE = #{bidPrice}
  AND BID_CONTACT_NAME = #{bidContactName}
  AND BID_CONTACT_PHONE = #{bidContactPhone}
  AND BID_STATUS = #{bidStatus}
