mapper.update:
UPDATE BID_DESC
SET
  BID_DESC_TEXT = #{bidDescText},
  BID_DESC_MEDIA = #{bidDescMedia},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},
  UPDATE_DATETIME = current_timestamp() 
WHERE UPDATE_DATETIME = #{updateDatetime}
  AND REQ_CD = #{reqCd}
  AND DESC_SEQ = #{descSeq}
  AND BID_UID = #{bidUid}
   AND UPDATE_DATETIME = #{updateDatetime}

UPDATE BID_DESC

  BID_DESC_TEXT = #{bidDescText},
  BID_DESC_MEDIA = #{bidDescMedia},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},
  UPDATE_DATETIME = current_timestamp()

WHERE 
   REQ_CD = #{reqCd}
   AND  DESC_SEQ = #{descSeq}
   AND  BID_UID = #{bidUid}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  REQ_CD AS "reqCd",
  DESC_SEQ AS "descSeq",
  BID_UID AS "bidUid",
  BID_DESC_TEXT AS "bidDescText",
  BID_DESC_MEDIA AS "bidDescMedia",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM BID_DESC
WHERE 1 = 1 
  AND REQ_CD = #{reqCd}
  AND DESC_SEQ = #{descSeq}
  AND BID_UID = #{bidUid}
  AND BID_DESC_TEXT = #{bidDescText}
  AND BID_DESC_MEDIA = #{bidDescMedia}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM BID_DESC
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND REQ_CD = #{reqCd}
  AND DESC_SEQ = #{descSeq}
  AND BID_UID = #{bidUid}
  AND BID_DESC_TEXT = #{bidDescText}
  AND BID_DESC_MEDIA = #{bidDescMedia}
