mapper.update:
UPDATE REQ
PRODUCT_CD = #{productCd}, PRODUCT_SPECS = #{productSpecs}, PRODUCT_QUANTITY = #{productQuantity}, PRODUCT_ESTIMATION = #{productEstimation}, CONTACT_ADDRESS = #{contactAddress}, CONTACT_NAME = #{contactName}, CONTACT_PHONE = #{contactPhone}, BID_DATE_BEGIN = #{bidDateBegin}, BID_DATE_END = #{bidDateEnd}, BID_MAX_LIMIT = #{bidMaxLimit}, BID_STATUS = #{bidStatus}, BID_UID = #{bidUid}, BID_PRICE = #{bidPrice}, LOGISTICS_CD = #{logisticsCd}, LOGISTICS_DESC = #{logisticsDesc}, LOGISTICS_STATUS = #{logisticsStatus}, COMMENT = #{comment}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE REQ_CD = #{reqCd} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  REQ_CD AS "reqCd",
  PRODUCT_CD AS "productCd",
  PRODUCT_SPECS AS "productSpecs",
  PRODUCT_QUANTITY AS "productQuantity",
  PRODUCT_ESTIMATION AS "productEstimation",
  CONTACT_ADDRESS AS "contactAddress",
  CONTACT_NAME AS "contactName",
  CONTACT_PHONE AS "contactPhone",
  BID_DATE_BEGIN AS "bidDateBegin",
  BID_DATE_END AS "bidDateEnd",
  BID_MAX_LIMIT AS "bidMaxLimit",
  BID_STATUS AS "bidStatus",
  BID_UID AS "bidUid",
  BID_PRICE AS "bidPrice",
  LOGISTICS_CD AS "logisticsCd",
  LOGISTICS_DESC AS "logisticsDesc",
  LOGISTICS_STATUS AS "logisticsStatus",
  COMMENT AS "comment",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM REQ
WHERE 1 = 1 
  AND REQ_CD = #{reqCd}
  AND PRODUCT_CD = #{productCd}
  AND PRODUCT_SPECS = #{productSpecs}
  AND PRODUCT_QUANTITY = #{productQuantity}
  AND PRODUCT_ESTIMATION = #{productEstimation}
  AND CONTACT_ADDRESS = #{contactAddress}
  AND CONTACT_NAME = #{contactName}
  AND CONTACT_PHONE = #{contactPhone}
  AND BID_DATE_BEGIN = #{bidDateBegin}
  AND BID_DATE_END = #{bidDateEnd}
  AND BID_MAX_LIMIT = #{bidMaxLimit}
  AND BID_STATUS = #{bidStatus}
  AND BID_UID = #{bidUid}
  AND BID_PRICE = #{bidPrice}
  AND LOGISTICS_CD = #{logisticsCd}
  AND LOGISTICS_DESC = #{logisticsDesc}
  AND LOGISTICS_STATUS = #{logisticsStatus}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM REQ
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND REQ_CD = #{reqCd}
  AND PRODUCT_CD = #{productCd}
  AND PRODUCT_SPECS = #{productSpecs}
  AND PRODUCT_QUANTITY = #{productQuantity}
  AND PRODUCT_ESTIMATION = #{productEstimation}
  AND CONTACT_ADDRESS = #{contactAddress}
  AND CONTACT_NAME = #{contactName}
  AND CONTACT_PHONE = #{contactPhone}
  AND BID_DATE_BEGIN = #{bidDateBegin}
  AND BID_DATE_END = #{bidDateEnd}
  AND BID_MAX_LIMIT = #{bidMaxLimit}
  AND BID_STATUS = #{bidStatus}
  AND BID_UID = #{bidUid}
  AND BID_PRICE = #{bidPrice}
  AND LOGISTICS_CD = #{logisticsCd}
  AND LOGISTICS_DESC = #{logisticsDesc}
  AND LOGISTICS_STATUS = #{logisticsStatus}
  AND COMMENT = #{comment}
