mapper.update:
UPDATE REQ_DESC
DESC_TYPE = #{descType}, DESC_TAG = #{descTag}, DESC_TEXT = #{descText}, DESC_MEDIA = #{descMedia}, OPTIONAL = #{optional}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE REQ_CD = #{reqCd} AND DESC_SEQ = #{descSeq} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  REQ_CD AS "reqCd",
  DESC_SEQ AS "descSeq",
  DESC_TYPE AS "descType",
  DESC_TAG AS "descTag",
  DESC_TEXT AS "descText",
  DESC_MEDIA AS "descMedia",
  OPTIONAL AS "optional",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM REQ_DESC
WHERE 1 = 1 
  AND REQ_CD = #{reqCd}
  AND DESC_SEQ = #{descSeq}
  AND DESC_TYPE = #{descType}
  AND DESC_TAG = #{descTag}
  AND DESC_TEXT = #{descText}
  AND DESC_MEDIA = #{descMedia}
  AND OPTIONAL = #{optional}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM REQ_DESC
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND REQ_CD = #{reqCd}
  AND DESC_SEQ = #{descSeq}
  AND DESC_TYPE = #{descType}
  AND DESC_TAG = #{descTag}
  AND DESC_TEXT = #{descText}
  AND DESC_MEDIA = #{descMedia}
  AND OPTIONAL = #{optional}
