mapper.update:
UPDATE REQ_PUB
SET
  COMMENT = #{comment},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},
  UPDATE_DATETIME = current_timestamp() 
WHERE UPDATE_DATETIME = #{updateDatetime}
  AND PUB_CD = #{pubCd}
  AND REQ_CD = #{reqCd}
   AND UPDATE_DATETIME = #{updateDatetime}

UPDATE REQ_PUB

  COMMENT = #{comment},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},
  UPDATE_DATETIME = current_timestamp()

WHERE 
   PUB_CD = #{pubCd}
   AND  REQ_CD = #{reqCd}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  PUB_CD AS "pubCd",
  REQ_CD AS "reqCd",
  COMMENT AS "comment",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM REQ_PUB
WHERE 1 = 1 
  AND PUB_CD = #{pubCd}
  AND REQ_CD = #{reqCd}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM REQ_PUB
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND PUB_CD = #{pubCd}
  AND REQ_CD = #{reqCd}
  AND COMMENT = #{comment}
